import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../utils/AuthContext';
import api from '../services/api';

const Dashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    totalTeams: 0,
    activeGames: 0,
    completedQuizzes: 0,
    averageScore: 0
  });
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchRealStats();
  }, []);

  const fetchRealStats = async () => {
    try {
      setIsLoading(true);

      // Get real stats from API
      const [teamsRes, submissionsRes, quizzesRes] = await Promise.all([
        api.get('/api/teams'),
        api.get('/api/leaderboard'),
        api.get('/api/quiz')
      ]);

      const teams = teamsRes.data || [];
      const submissions = submissionsRes.data || [];
      const quizzes = quizzesRes.data || [];

      // Calculate average score
      const avgScore = submissions.length > 0 
        ? Math.round(submissions.reduce((sum, sub) => sum + (sub.percentage || 0), 0) / submissions.length)
        : 0;

      setStats({
        totalTeams: teams.length,
        activeGames: quizzes.filter(q => q.is_published).length,
        completedQuizzes: submissions.length,
        averageScore: avgScore
      });
    } catch (error) {
      console.error('Error fetching stats:', error);
      // Set to zero for fresh startup
      setStats({
        totalTeams: 0,
        activeGames: 0,
        completedQuizzes: 0,
        averageScore: 0
      });
    } finally {
      setIsLoading(false);
    }
  };

  const quickActions = [
    { to: '/teams', icon: 'ðŸ‘¥', title: 'Manage Teams', desc: 'Create and organize your escape room teams', color: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' },
    { to: '/game', icon: 'ðŸŽ®', title: 'Play Game', desc: 'Start your escape room adventure', color: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)' },
    { to: '/results', icon: 'ðŸ“Š', title: 'View Results', desc: 'Check performance and statistics', color: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)' },
    { to: '/live', icon: 'ðŸ”´', title: 'Live Board', desc: 'Real-time leaderboard updates', color: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)' }
  ];

  const adminActions = [
    { to: '/admin', icon: 'âš¡', title: 'Admin Panel', desc: 'System administration', color: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)' },
    { to: '/quiz-creator', icon: 'âž•', title: 'Create Quiz', desc: 'Design new challenges', color: 'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)' },
    { to: '/quiz-list', icon: 'ðŸ“‹', title: 'Quiz List', desc: 'Manage all quizzes', color: 'linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%)' },
    { to: '/settings', icon: 'âš™ï¸', title: 'Settings', desc: 'Configure system settings', color: 'linear-gradient(135deg, #a8c0ff 0%, #3f2b96 100%)' }
  ];

  const StatCard = ({ icon, title, value, subtitle, delay }) => (
    <div 
      className="dashboard-card fade-in" 
      style={{ 
        animationDelay: `${delay}s`,
        background: 'rgba(255, 255, 255, 0.95)',
        textAlign: 'center'
      }}
    >
      <div style={{ fontSize: '3rem', marginBottom: '16px' }}>{icon}</div>
      <h3 style={{ 
        fontSize: '2.5rem', 
        fontWeight: '700', 
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent',
        backgroundClip: 'text',
        marginBottom: '8px'
      }}>
        {isLoading ? <div className="loading"></div> : value}
      </h3>
      <h4 style={{ color: '#4a5568', fontWeight: '600', marginBottom: '4px' }}>{title}</h4>
      <p style={{ color: '#718096', fontSize: '14px' }}>{subtitle}</p>
    </div>
  );

  const ActionCard = ({ to, icon, title, desc, color, delay }) => (
    <Link 
      to={to} 
      className="action-btn fade-in" 
      style={{ 
        background: color,
        animationDelay: `${delay}s`,
        textDecoration: 'none',
        display: 'block'
      }}
    >
      <div style={{ fontSize: '2rem', marginBottom: '12px' }}>{icon}</div>
      <h3 style={{ fontSize: '18px', fontWeight: '700', marginBottom: '8px' }}>{title}</h3>
      <p style={{ fontSize: '14px', opacity: '0.9', margin: '0' }}>{desc}</p>
    </Link>
  );

  return (
    <div className="container">
      {/* Hero Section */}
      <div style={{ textAlign: 'center', marginBottom: '60px' }}>
        <h1 className="welcome-title">
          Welcome back, {user?.name}! ðŸŽ‰
        </h1>
        <p style={{ 
          fontSize: '18px', 
          color: '#718096', 
          maxWidth: '600px', 
          margin: '0 auto',
          lineHeight: '1.6'
        }}>
          Ready to challenge your mind? Dive into exciting escape room adventures and test your problem-solving skills.
        </p>
      </div>

      {/* Stats Dashboard */}
      <div className="dashboard-grid" style={{ marginBottom: '60px' }}>
        <StatCard 
          icon="ðŸ‘¥" 
          title="Total Teams" 
          value={stats.totalTeams} 
          subtitle="Active teams in system"
          delay={0.1}
        />
        <StatCard 
          icon="ðŸŽ®" 
          title="Active Games" 
          value={stats.activeGames} 
          subtitle="Currently running"
          delay={0.2}
        />
        <StatCard 
          icon="ðŸ†" 
          title="Completed" 
          value={stats.completedQuizzes} 
          subtitle="Total quiz attempts"
          delay={0.3}
        />
        <StatCard 
          icon="â­" 
          title="Avg Score" 
          value={`${stats.averageScore}%`} 
          subtitle="Overall performance"
          delay={0.4}
        />
      </div>

      {/* Quick Actions */}
      <div className="card fade-in" style={{ animationDelay: '0.5s' }}>
        <h2 style={{ 
          fontSize: '2rem', 
          fontWeight: '700', 
          marginBottom: '30px',
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text'
        }}>
          ðŸš€ Quick Actions
        </h2>
        <div className="quick-actions">
          {quickActions.map((action, index) => (
            <ActionCard 
              key={action.to}
              {...action}
              delay={0.6 + index * 0.1}
            />
          ))}
        </div>
      </div>

      {/* Admin Section */}
      {user?.email === 'admin@escaperoom.com' && (
        <div className="card fade-in" style={{ animationDelay: '1s' }}>
          <h2 style={{ 
            fontSize: '2rem', 
            fontWeight: '700', 
            marginBottom: '30px',
            background: 'linear-gradient(135deg, #f56565 0%, #e53e3e 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            ðŸ”’ Admin Controls
          </h2>
          <div className="quick-actions">
            {adminActions.map((action, index) => (
              <ActionCard 
                key={action.to}
                {...action}
                delay={1.1 + index * 0.1}
              />
            ))}
          </div>
        </div>
      )}

      {/* Motivational Quote */}
      <div className="card fade-in" style={{ 
        animationDelay: '1.5s',
        textAlign: 'center',
        background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%)',
        border: '2px solid rgba(102, 126, 234, 0.2)'
      }}>
        <h3 style={{ 
          fontSize: '1.5rem', 
          fontWeight: '600', 
          color: '#4a5568',
          marginBottom: '16px'
        }}>
          ðŸ’¡ "The key to escape is not just in solving puzzles, but in thinking outside the box."
        </h3>
        <p style={{ color: '#718096', fontSize: '16px' }}>Ready to unlock your potential?</p>
      </div>
    </div>
  );
};

export default Dashboard;
