import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../utils/AuthContext';

const Dashboard = () => {
  const { user } = useAuth();

  return (
    <div className="container">
      <h1>Welcome, {user?.name}!</h1>

      <div className="card">
        <h2>Quick Actions</h2>
        <div style={{ display: 'flex', gap: '16px', flexWrap: 'wrap' }}>
          <Link to="/teams" className="btn btn-primary">Manage Teams</Link>
          <Link to="/game" className="btn btn-primary">Play Game</Link>
          <Link to="/results" className="btn btn-primary">View Results</Link>
          <Link to="/live" className="btn btn-primary">Live Leaderboard</Link>
          {user?.email === 'admin@escaperoom.com' && (
            <>
              <Link to="/admin" className="btn" style={{ backgroundColor: '#dc3545', color: 'white' }}>Admin Panel</Link>
              <Link to="/quiz-creator" className="btn" style={{ backgroundColor: '#dc3545', color: 'white' }}>Create Quiz</Link>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
