import React, { useState, useEffect } from 'react';
import api from '../services/api';

const AdminDashboard = () => {
  const [teams, setTeams] = useState([]);
  const [analytics, setAnalytics] = useState(null);
  const [announcements, setAnnouncements] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [teamSubmissions, setTeamSubmissions] = useState([]);
  const [newAnnouncement, setNewAnnouncement] = useState({ title: '', message: '', type: 'info' });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [teamsRes, analyticsRes, announcementsRes] = await Promise.all([
        api.get('/admin/teams'),
        api.get('/admin/analytics'),
        api.get('/admin/announcements')
      ]);
      setTeams(teamsRes.data);
      setAnalytics(analyticsRes.data);
      setAnnouncements(announcementsRes.data);
    } catch (error) {
      console.error('Error fetching admin data:', error);
    } finally {
      setLoading(false);
    }
  };

  const toggleTeamLock = async (teamId, currentLocked) => {
    try {
      await api.post(`/admin/teams/${teamId}/lock`, { locked: !currentLocked });
      fetchData();
    } catch (error) {
      console.error('Error toggling team lock:', error);
    }
  };

  const advanceTeam = async (teamId, stage) => {
    try {
      await api.post(`/admin/teams/${teamId}/advance`, { stage });
      fetchData();
    } catch (error) {
      console.error('Error advancing team:', error);
    }
  };

  const viewTeamSubmissions = async (teamId) => {
    try {
      const response = await api.get(`/admin/teams/${teamId}/submissions`);
      setTeamSubmissions(response.data);
      setSelectedTeam(teamId);
    } catch (error) {
      console.error('Error fetching submissions:', error);
    }
  };

  const createAnnouncement = async (e) => {
    e.preventDefault();
    try {
      await api.post('/admin/announcements', newAnnouncement);
      setNewAnnouncement({ title: '', message: '', type: 'info' });
      fetchData();
    } catch (error) {
      console.error('Error creating announcement:', error);
    }
  };

  const endEvent = async () => {
    if (!window.confirm('Are you sure you want to end the event? This will finalize results and generate certificates.')) {
      return;
    }

    try {
      const response = await api.post('/results/end-event');
      alert(`Event ended successfully! Winner: ${response.data.winner?.name || 'No winner'}`);
      fetchData();
    } catch (error) {
      console.error('Error ending event:', error);
      alert('Error ending event');
    }
  };

  if (loading) return <div className="container">Loading admin dashboard...</div>;

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1>üõ†Ô∏è Admin Dashboard</h1>
        <button
          onClick={endEvent}
          style={{
            padding: '10px 20px',
            backgroundColor: '#dc3545',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '16px'
          }}
        >
          üèÅ End Event
        </button>
      </div>

      {/* Analytics Overview */}
      <div className="card">
        <h2>üìä Analytics Overview</h2>
        {analytics && (
          <>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px', marginBottom: '20px' }}>
              <div style={{ textAlign: 'center', padding: '20px', backgroundColor: '#e7f3ff', borderRadius: '8px' }}>
                <h3>{analytics.completionStats.total_teams}</h3>
                <p>Total Teams</p>
              </div>
              <div style={{ textAlign: 'center', padding: '20px', backgroundColor: '#d4edda', borderRadius: '8px' }}>
                <h3>{analytics.completionStats.completed_teams}</h3>
                <p>Completed</p>
              </div>
              <div style={{ textAlign: 'center', padding: '20px', backgroundColor: '#fff3cd', borderRadius: '8px' }}>
                <h3>{Math.round(analytics.completionStats.avg_completion_time_minutes || 0)}m</h3>
                <p>Avg Time</p>
              </div>
            </div>

            {/* Stage Difficulty Analysis */}
            <h3>Stage Difficulty Analysis</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '16px' }}>
              {analytics.stageAnalytics.map(stage => (
                <div key={stage.stage_number} style={{ 
                  padding: '16px', 
                  backgroundColor: '#f8f9fa',
                  borderRadius: '8px',
                  border: '1px solid #ddd'
                }}>
                  <h4>Stage {stage.stage_number}</h4>
                  <div style={{ fontSize: '14px' }}>
                    <div>Attempts: {stage.total_attempts}</div>
                    <div>Correct: {stage.correct_attempts}</div>
                    <div style={{ 
                      color: stage.success_rate > 70 ? '#28a745' : 
                             stage.success_rate > 40 ? '#ffc107' : '#dc3545',
                      fontWeight: 'bold'
                    }}>
                      Success Rate: {stage.success_rate}%
                    </div>
                  </div>
                  <div style={{ 
                    width: '100%', 
                    height: '6px', 
                    backgroundColor: '#e9ecef',
                    borderRadius: '3px',
                    marginTop: '8px',
                    overflow: 'hidden'
                  }}>
                    <div style={{
                      width: `${stage.success_rate}%`,
                      height: '100%',
                      backgroundColor: stage.success_rate > 70 ? '#28a745' : 
                                     stage.success_rate > 40 ? '#ffc107' : '#dc3545'
                    }} />
                  </div>
                </div>
              ))}
            </div>
          </>
        )}
      </div>

      {/* Team Management */}
      <div className="card">
        <h2>üë• Team Management</h2>
        <div style={{ overflowX: 'auto' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ backgroundColor: '#f8f9fa' }}>
                <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #ddd' }}>Team</th>
                <th style={{ padding: '12px', textAlign: 'center', border: '1px solid #ddd' }}>Stage</th>
                <th style={{ padding: '12px', textAlign: 'center', border: '1px solid #ddd' }}>Status</th>
                <th style={{ padding: '12px', textAlign: 'center', border: '1px solid #ddd' }}>Members</th>
                <th style={{ padding: '12px', textAlign: 'center', border: '1px solid #ddd' }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {teams.map(team => (
                <tr key={team.id}>
                  <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                    <strong>{team.name}</strong>
                    {team.is_locked && <span style={{ color: '#dc3545', marginLeft: '8px' }}>üîí</span>}
                  </td>
                  <td style={{ padding: '12px', textAlign: 'center', border: '1px solid #ddd' }}>
                    {team.current_stage || 1}/6
                  </td>
                  <td style={{ padding: '12px', textAlign: 'center', border: '1px solid #ddd' }}>
                    <span style={{ 
                      padding: '4px 8px', 
                      borderRadius: '4px',
                      backgroundColor: team.is_completed ? '#d4edda' : '#fff3cd',
                      color: team.is_completed ? '#155724' : '#856404'
                    }}>
                      {team.is_completed ? 'Completed' : 'In Progress'}
                    </span>
                  </td>
                  <td style={{ padding: '12px', textAlign: 'center', border: '1px solid #ddd' }}>
                    {team.member_count}
                  </td>
                  <td style={{ padding: '12px', textAlign: 'center', border: '1px solid #ddd' }}>
                    <div style={{ display: 'flex', gap: '8px', justifyContent: 'center', flexWrap: 'wrap' }}>
                      <button
                        onClick={() => toggleTeamLock(team.id, team.is_locked)}
                        style={{ 
                          padding: '4px 8px', 
                          fontSize: '12px',
                          backgroundColor: team.is_locked ? '#28a745' : '#dc3545',
                          color: 'white',
                          border: 'none',
                          borderRadius: '4px',
                          cursor: 'pointer'
                        }}
                      >
                        {team.is_locked ? 'Unlock' : 'Lock'}
                      </button>
                      <button
                        onClick={() => viewTeamSubmissions(team.id)}
                        style={{ 
                          padding: '4px 8px', 
                          fontSize: '12px',
                          backgroundColor: '#007bff',
                          color: 'white',
                          border: 'none',
                          borderRadius: '4px',
                          cursor: 'pointer'
                        }}
                      >
                        View
                      </button>
                      <select
                        onChange={(e) => advanceTeam(team.id, parseInt(e.target.value))}
                        value={team.current_stage || 1}
                        style={{ padding: '4px', fontSize: '12px' }}
                      >
                        {[1,2,3,4,5,6].map(stage => (
                          <option key={stage} value={stage}>Stage {stage}</option>
                        ))}
                      </select>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Team Submissions Modal */}
      {selectedTeam && (
        <div className="card">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <h2>üìù Team Submissions</h2>
            <button onClick={() => setSelectedTeam(null)} style={{ background: 'none', border: 'none', fontSize: '20px' }}>‚úï</button>
          </div>
          <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
            {teamSubmissions.map((submission, index) => (
              <div key={index} style={{ 
                padding: '12px', 
                borderBottom: '1px solid #eee',
                backgroundColor: submission.is_correct ? '#d4edda' : '#f8d7da'
              }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <span><strong>Stage {submission.stage_number}:</strong> {submission.stage_title}</span>
                  <span style={{ fontSize: '12px', color: '#666' }}>
                    {new Date(submission.submitted_at).toLocaleString()}
                  </span>
                </div>
                <div style={{ marginTop: '8px' }}>
                  <strong>Answer:</strong> {submission.submitted_answer}
                </div>
                <div style={{ fontSize: '12px', color: '#666' }}>
                  By: {submission.submitted_by} | {submission.is_correct ? '‚úÖ Correct' : '‚ùå Incorrect'}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Announcements */}
      <div className="card">
        <h2>üì¢ Announcements</h2>
        <form onSubmit={createAnnouncement} style={{ marginBottom: '20px' }}>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 100px', gap: '10px', marginBottom: '10px' }}>
            <input
              type="text"
              placeholder="Announcement title..."
              value={newAnnouncement.title}
              onChange={(e) => setNewAnnouncement({...newAnnouncement, title: e.target.value})}
              required
            />
            <input
              type="text"
              placeholder="Message..."
              value={newAnnouncement.message}
              onChange={(e) => setNewAnnouncement({...newAnnouncement, message: e.target.value})}
              required
            />
            <select
              value={newAnnouncement.type}
              onChange={(e) => setNewAnnouncement({...newAnnouncement, type: e.target.value})}
            >
              <option value="info">Info</option>
              <option value="warning">Warning</option>
              <option value="success">Success</option>
              <option value="winner">Winner</option>
            </select>
          </div>
          <button type="submit" className="btn btn-primary">Create Announcement</button>
        </form>

        <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
          {announcements.map(announcement => (
            <div key={announcement.id} style={{ 
              padding: '12px', 
              marginBottom: '10px',
              borderRadius: '4px',
              backgroundColor: {
                info: '#e7f3ff',
                warning: '#fff3cd',
                success: '#d4edda',
                winner: '#f8d7da'
              }[announcement.announcement_type]
            }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <strong>{announcement.title}</strong>
                <span style={{ fontSize: '12px', color: '#666' }}>
                  {new Date(announcement.created_at).toLocaleString()}
                </span>
              </div>
              <p style={{ margin: '8px 0 0 0' }}>{announcement.message}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
