import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../utils/AuthContext';
import api from '../services/api';

const Dashboard = () => {
  const { user } = useAuth();
  const [teams, setTeams] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTeams();
  }, []);

  const fetchTeams = async () => {
    try {
      const response = await api.get('/teams');
      setTeams(response.data);
    } catch (error) {
      console.error('Error fetching teams:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="container">Loading...</div>;
  }

  return (
    <div className="container">
      <h1>Welcome, {user?.name}!</h1>
      <p><strong>College ID:</strong> {user?.college_id}</p>

      <div className="card">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h2>Your Teams</h2>
          {teams.length > 0 && (
            <Link to="/game" className="btn btn-primary" style={{ textDecoration: 'none' }}>
              ðŸŽ® Start Game
            </Link>
          )}
        </div>
        {teams.length === 0 ? (
          <p>You're not part of any teams yet. Create or join a team to get started!</p>
        ) : (
          <div className="team-list">
            {teams.map(team => (
              <div key={team.id} className="card">
                <h3>{team.name}</h3>
                <p>{team.description}</p>
                <p><strong>Role:</strong> {team.role}</p>
                <p><strong>Created by:</strong> {team.created_by_name}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
