import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../utils/AuthContext';
import { useSettings } from '../utils/SettingsContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const { settings } = useSettings();
  const navigate = useNavigate();
  const location = useLocation();
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const navLinkStyle = {
    textDecoration: 'none',
    color: '#4a5568',
    fontWeight: '600',
    fontSize: '15px',
    padding: '8px 16px',
    borderRadius: '8px',
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
    position: 'relative',
    textTransform: 'uppercase',
    letterSpacing: '0.5px'
  };

  const activeNavLinkStyle = {
    ...navLinkStyle,
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    color: 'white',
    boxShadow: '0 4px 15px rgba(102, 126, 234, 0.3)'
  };

  const adminLinkStyle = {
    ...navLinkStyle,
    background: 'linear-gradient(135deg, #f56565 0%, #e53e3e 100%)',
    color: 'white',
    boxShadow: '0 4px 15px rgba(245, 101, 101, 0.3)'
  };

  const isActive = (path) => location.pathname === path;

  return (
    <>
      <nav className="navbar">
        <div className="navbar-content">
          {settings.showQuizName && (
            <Link to="/" style={{
              textDecoration: 'none',
              background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              backgroundClip: 'text',
              fontSize: '24px',
              fontWeight: '700',
              letterSpacing: '-0.5px'
            }}>
              üè¢ {settings.quizName || 'Escape Room Challenge'}
            </Link>
          )}

          {user ? (
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <Link 
                to="/teams" 
                style={isActive('/teams') ? activeNavLinkStyle : navLinkStyle}
                onMouseEnter={(e) => {
                  if (!isActive('/teams')) {
                    e.target.style.background = 'rgba(102, 126, 234, 0.1)';
                    e.target.style.transform = 'translateY(-2px)';
                  }
                }}
                onMouseLeave={(e) => {
                  if (!isActive('/teams')) {
                    e.target.style.background = 'transparent';
                    e.target.style.transform = 'translateY(0)';
                  }
                }}
              >
                üë• Teams
              </Link>
              <Link 
                to="/game" 
                style={isActive('/game') ? activeNavLinkStyle : navLinkStyle}
                onMouseEnter={(e) => {
                  if (!isActive('/game')) {
                    e.target.style.background = 'rgba(102, 126, 234, 0.1)';
                    e.target.style.transform = 'translateY(-2px)';
                  }
                }}
                onMouseLeave={(e) => {
                  if (!isActive('/game')) {
                    e.target.style.background = 'transparent';
                    e.target.style.transform = 'translateY(0)';
                  }
                }}
              >
                üéÆ Game
              </Link>
              <Link 
                to="/results" 
                style={isActive('/results') ? activeNavLinkStyle : navLinkStyle}
                onMouseEnter={(e) => {
                  if (!isActive('/results')) {
                    e.target.style.background = 'rgba(102, 126, 234, 0.1)';
                    e.target.style.transform = 'translateY(-2px)';
                  }
                }}
                onMouseLeave={(e) => {
                  if (!isActive('/results')) {
                    e.target.style.background = 'transparent';
                    e.target.style.transform = 'translateY(0)';
                  }
                }}
              >
                üìä Results
              </Link>
              <Link 
                to="/live" 
                style={isActive('/live') ? activeNavLinkStyle : navLinkStyle}
                onMouseEnter={(e) => {
                  if (!isActive('/live')) {
                    e.target.style.background = 'rgba(102, 126, 234, 0.1)';
                    e.target.style.transform = 'translateY(-2px)';
                  }
                }}
                onMouseLeave={(e) => {
                  if (!isActive('/live')) {
                    e.target.style.background = 'transparent';
                    e.target.style.transform = 'translateY(0)';
                  }
                }}
              >
                üî¥ Live
              </Link>
              {user.email === 'admin@escaperoom.com' && (
                <>
                  <Link to="/admin" style={adminLinkStyle}>‚ö° Admin</Link>
                  <Link to="/quiz-creator" style={adminLinkStyle}>‚ûï Create Quiz</Link>
                  <Link to="/quiz-list" style={adminLinkStyle}>üìã Quiz List</Link>
                  <Link to="/settings" style={adminLinkStyle}>‚öôÔ∏è Settings</Link>
                </>
              )}
              <div style={{
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                color: 'white',
                padding: '8px 16px',
                borderRadius: '20px',
                fontSize: '14px',
                fontWeight: '600',
                marginLeft: '16px'
              }}>
                üëã {user.name}
              </div>
              <button 
                onClick={handleLogout} 
                className="btn btn-primary"
                style={{
                  background: 'linear-gradient(135deg, #f56565 0%, #e53e3e 100%)',
                  boxShadow: '0 8px 25px rgba(245, 101, 101, 0.3)'
                }}
              >
                üö™ Logout
              </button>
            </div>
          ) : (
            <div style={{ display: 'flex', gap: '12px' }}>
              <Link to="/login" className="btn btn-primary">üîë Login</Link>
              <Link to="/register" className="btn btn-primary">üìù Register</Link>
            </div>
          )}
        </div>
      </nav>
    </>
  );
};

export default Navbar;
