import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../utils/AuthContext';

const Register = () => {
  const [formData, setFormData] = useState({ 
    email: '', password: '', name: '', teamName: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [step, setStep] = useState(1);
  const { register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (error) setError('');
  };

  const handleNext = () => {
    if (step === 1 && formData.name && formData.email) {
      setStep(2);
    }
  };

  const handleBack = () => {
    setStep(1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      await register(formData.email, formData.password, formData.name, formData.teamName);
      navigate('/');
    } catch (error) {
      setError('Registration failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container" style={{ 
      minHeight: '100vh', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      padding: '20px'
    }}>
      {/* Background Animation */}
      <div style={{
        position: 'absolute',
        top: '15%',
        right: '10%',
        width: '180px',
        height: '180px',
        background: 'linear-gradient(45deg, rgba(245, 101, 101, 0.1), rgba(229, 62, 62, 0.1))',
        borderRadius: '50%',
        animation: 'float 7s ease-in-out infinite',
        zIndex: -1
      }}></div>

      <div style={{
        position: 'absolute',
        bottom: '10%',
        left: '15%',
        width: '120px',
        height: '120px',
        background: 'linear-gradient(45deg, rgba(67, 233, 123, 0.1), rgba(56, 249, 215, 0.1))',
        borderRadius: '20px',
        animation: 'float 9s ease-in-out infinite reverse',
        zIndex: -1
      }}></div>

      <div className="card fade-in" style={{ 
        maxWidth: '500px', 
        width: '100%',
        background: 'rgba(255, 255, 255, 0.95)',
        backdropFilter: 'blur(20px)',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        boxShadow: '0 25px 50px rgba(0, 0, 0, 0.15)'
      }}>
        {/* Header */}
        <div style={{ textAlign: 'center', marginBottom: '40px' }}>
          <div style={{ 
            fontSize: '4rem', 
            marginBottom: '16px',
            animation: 'bounce 2s infinite'
          }}>
            üöÄ
          </div>
          <h2 style={{ 
            fontSize: '2.5rem',
            fontWeight: '700',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text',
            marginBottom: '8px'
          }}>
            Join EscapeRoom Pro
          </h2>
          <p style={{ color: '#718096', fontSize: '16px' }}>
            Create your account and start your adventure
          </p>
        </div>

        {/* Progress Indicator */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          marginBottom: '32px',
          gap: '12px'
        }}>
          <div style={{
            width: '40px',
            height: '4px',
            borderRadius: '2px',
            background: step >= 1 ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' : 'rgba(0, 0, 0, 0.1)',
            transition: 'all 0.3s ease'
          }}></div>
          <div style={{
            width: '40px',
            height: '4px',
            borderRadius: '2px',
            background: step >= 2 ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' : 'rgba(0, 0, 0, 0.1)',
            transition: 'all 0.3s ease'
          }}></div>
        </div>

        {error && (
          <div className="error-message fade-in">
            ‚ö†Ô∏è {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          {step === 1 && (
            <div className="fade-in">
              <h3 style={{ 
                color: '#4a5568', 
                marginBottom: '24px',
                fontSize: '18px',
                fontWeight: '600'
              }}>
                üë§ Personal Information
              </h3>

              <div className="form-group">
