import React, { useState, useEffect } from 'react';
import api from '../services/api';
import CountdownTimer from '../components/CountdownTimer';
import ProgressBar from '../components/ProgressBar';
import SecurityWrapper from '../components/SecurityWrapper';
import BinaryPuzzle from '../components/puzzles/BinaryPuzzle';
import LogicPuzzle from '../components/puzzles/LogicPuzzle';
import SQLPuzzle from '../components/puzzles/SQLPuzzle';
import CryptographyPuzzle from '../components/puzzles/CryptographyPuzzle';
import FinalEscapePuzzle from '../components/puzzles/FinalEscapePuzzle';
import TeamChat from '../components/TeamChat';
import HintSystem from '../components/HintSystem';

const GameDashboard = () => {
  const [gameState, setGameState] = useState(null);
  const [answer, setAnswer] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    fetchGameStatus();
  }, []);

  const fetchGameStatus = async () => {
    try {
      const response = await api.get('/game/status');
      setGameState(response.data);
    } catch (error) {
      console.error('Error fetching game status:', error);
      setMessage('Error loading game. Please refresh the page.');
    } finally {
      setLoading(false);
    }
  };

  const submitAnswer = async (e, submittedAnswer = null) => {
    e?.preventDefault();
    const answerToSubmit = submittedAnswer || answer;
    if (!answerToSubmit?.trim()) return;

    setSubmitting(true);
    setMessage('');

    try {
      const response = await api.post('/game/submit', { answer: answerToSubmit });
      const result = response.data;

      setMessage(result.message);

      if (result.correct) {
        setAnswer('');
        // Refresh game state after correct answer
        setTimeout(() => {
          fetchGameStatus();
          setMessage('');
        }, 2000);
      }
    } catch (error) {
      setMessage(error.response?.data?.error || 'Error submitting answer');
    } finally {
      setSubmitting(false);
    }
  };

  const handleTimeUp = () => {
    setMessage('Time is up! The game has ended.');
  };

  if (loading) {
    return <div className="container">Loading game...</div>;
  }

  if (!gameState) {
    return <div className="container">Unable to load game. Please try again.</div>;
  }

  const { team, currentStage, completedStages, isCompleted, timeRemaining, puzzle, totalStages } = gameState;

  const renderPuzzle = () => {
    const puzzleProps = {
      puzzle,
      onSubmit: (submittedAnswer) => {
        const event = { preventDefault: () => {} };
        submitAnswer(event, submittedAnswer);
      },
      submitting,
      message
    };

    switch (puzzle?.category) {
      case 'binary':
        return <BinaryPuzzle {...puzzleProps} />;
      case 'logic':
        return <LogicPuzzle {...puzzleProps} />;
      case 'sql':
        return <SQLPuzzle {...puzzleProps} />;
      case 'cryptography':
        return <CryptographyPuzzle {...puzzleProps} />;
      case 'final':
        return <FinalEscapePuzzle puzzle={puzzle} gameState={gameState} />;
      default:
        return (
          <div className="card">
            <h3>General Puzzle</h3>
            <p>{puzzle?.puzzle_text}</p>
            <form onSubmit={submitAnswer}>
              <div className="form-group">
                <input
                  type="text"
                  value={answer}
                  onChange={(e) => setAnswer(e.target.value)}
                  placeholder="Enter your answer..."
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary">Submit</button>
            </form>
            {message && <div>{message}</div>}
          </div>
        );
    }
  };

  if (isCompleted) {
    return (
      <div className="container">
        <div className="card" style={{ textAlign: 'center', padding: '40px' }}>
          <h1 style={{ color: '#28a745', marginBottom: '20px' }}>üéâ Congratulations!</h1>
          <h2>Team {team.name} has completed all stages!</h2>
          <p style={{ fontSize: '18px', marginTop: '20px' }}>
            You successfully solved all {totalStages} puzzles!
          </p>
        </div>
      </div>
    );
  }

  if (timeRemaining <= 0) {
    return (
      <div className="container">
        <div className="card" style={{ textAlign: 'center', padding: '40px' }}>
          <h1 style={{ color: '#dc3545', marginBottom: '20px' }}>‚è∞ Time's Up!</h1>
          <h2>Team {team.name}</h2>
          <p style={{ fontSize: '18px', marginTop: '20px' }}>
            You completed {completedStages.length} out of {totalStages} stages.
          </p>
        </div>
      </div>
    );
  }

  return (
    <SecurityWrapper disableCopy={true} disableRightClick={true}>
      <div className="container">
      <div className="game-layout" style={{ display: 'grid', gridTemplateColumns: '1fr 300px', gap: '20px' }}>
        {/* Main Game Area */}
        <div>
          {/* Header */}
          <div className="card">
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <h1>Team: {team.name}</h1>
                <h2>Stage {currentStage}: {puzzle?.title}</h2>
              </div>
              <CountdownTimer timeRemaining={timeRemaining} onTimeUp={handleTimeUp} />
            </div>
          </div>

          {/* Puzzle */}
          {renderPuzzle()}
        </div>

        {/* Sidebar */}
        <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
          <ProgressBar 
            currentStage={currentStage}
            completedStages={completedStages}
            totalStages={totalStages}
            puzzle={puzzle}
          />

          <HintSystem 
            teamId={team?.id}
            stage={currentStage}
          />

          <TeamChat 
            teamId={team?.id}
            stage={currentStage}
          />
        </div>
      </div>
      </div>
    </SecurityWrapper>
  );
};

export default GameDashboard;
