import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Teams = () => {
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    fetchTeams();
  }, []);

  const fetchTeams = async () => {
    try {
      const response = await api.get('/teams');
      setTeams(response.data);
    } catch (error) {
      console.error('Error fetching teams:', error);
    }
  };

  return (
    <div className="container">
      <h1>Teams</h1>

      <div className="card">
        <h2>All Teams</h2>
        {teams.length === 0 ? (
          <p>No teams found</p>
        ) : (
          <div>
            {teams.map(team => (
              <div key={team.id} className="card" style={{ marginBottom: '16px' }}>
                <h3>{team.name}</h3>
                <p>{team.description}</p>
                <p><strong>Created by:</strong> {team.created_by_name}</p>
                <p><strong>Members:</strong> {team.member_count}</p>
                <p><strong>Created:</strong> {new Date(team.created_at).toLocaleDateString()}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Teams;
