import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../utils/AuthContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="navbar-content">
        <Link to="/" style={{ textDecoration: 'none', color: '#333', fontSize: '20px', fontWeight: 'bold' }}>
          Escape Room App
        </Link>

        {user ? (
          <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
            <Link to="/teams" style={{ textDecoration: 'none', color: '#333' }}>Teams</Link>
            <Link to="/game" style={{ textDecoration: 'none', color: '#333' }}>Game</Link>
            <Link to="/results" style={{ textDecoration: 'none', color: '#333' }}>Results</Link>
            <Link to="/live" style={{ textDecoration: 'none', color: '#333' }}>Live</Link>
            {user.email === 'admin@escaperoom.com' && (
              <>
                <Link to="/admin" style={{ textDecoration: 'none', color: '#dc3545', fontWeight: 'bold' }}>Admin</Link>
                <Link to="/quiz-creator" style={{ textDecoration: 'none', color: '#dc3545', fontWeight: 'bold' }}>Create Quiz</Link>
                <Link to="/quiz-list" style={{ textDecoration: 'none', color: '#dc3545', fontWeight: 'bold' }}>Quiz List</Link>
              </>
            )}
            <span>Welcome, {user.name}</span>
            <button onClick={handleLogout} className="btn btn-primary">
              Logout
            </button>
          </div>
        ) : (
          <div style={{ display: 'flex', gap: '10px' }}>
            <Link to="/login" className="btn btn-primary">Login</Link>
            <Link to="/register" className="btn btn-primary">Register</Link>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
