import React, { useState, useEffect } from 'react';
import api from '../services/api';

const HintSystem = ({ teamId, stage }) => {
  const [hints, setHints] = useState([]);
  const [usedHints, setUsedHints] = useState([]);
  const [totalPenalty, setTotalPenalty] = useState(0);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (teamId && stage) {
      fetchHints();
    }
  }, [teamId, stage]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchHints = async () => {
    try {
      const response = await api.get(`/hints/${teamId}/${stage}`);
      setHints(response.data.hints);
      setUsedHints(response.data.usedHints);
      setTotalPenalty(response.data.totalPenalty);
    } catch (error) {
      console.error('Error fetching hints:', error);
    }
  };

  const handleUseHint = async (level) => {
    if (!window.confirm(`Using this hint will add ${level * 5} minutes penalty. Continue?`)) {
      return;
    }

    setLoading(true);
    try {
      await api.post(`/hints/${teamId}/${stage}/${level}`);
      fetchHints();
    } catch (error) {
      console.error('Error using hint:', error);
    } finally {
      setLoading(false);
    }
  };

  const isHintUsed = (level) => {
    return usedHints.some(h => h.hint_level === level);
  };

  return (
    <div style={{ 
      border: '1px solid #ffc107', 
      borderRadius: '8px', 
      padding: '16px',
      backgroundColor: '#fff3cd',
      marginBottom: '20px'
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '16px' }}>
        <h4 style={{ color: '#856404', margin: 0 }}>üí° Hint System</h4>
        {totalPenalty > 0 && (
          <span style={{ color: '#dc3545', fontWeight: 'bold' }}>
            ‚è±Ô∏è Total Penalty: {totalPenalty} minutes
          </span>
        )}
      </div>

      <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
        {hints.map(hint => (
          <div key={hint.level} style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            padding: '12px',
            backgroundColor: isHintUsed(hint.level) ? '#d4edda' : 'white',
            borderRadius: '4px',
            border: '1px solid #ddd'
          }}>
            <div style={{ flex: 1 }}>
              {isHintUsed(hint.level) ? (
                <div>
                  <strong>Hint {hint.level}:</strong> {hint.text}
                  <div style={{ fontSize: '12px', color: '#666', marginTop: '4px' }}>
                    Used (Penalty: {hint.penalty} minutes)
                  </div>
                </div>
              ) : (
                <div>
                  <strong>Hint {hint.level}</strong> - {hint.penalty} minute penalty
                </div>
              )}
            </div>

            {!isHintUsed(hint.level) && (
              <button
                onClick={() => handleUseHint(hint.level)}
                disabled={loading}
                style={{
                  padding: '6px 12px',
                  backgroundColor: '#ffc107',
                  color: '#856404',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: 'bold'
                }}
              >
                Use Hint
              </button>
            )}
          </div>
        ))}
      </div>

      <div style={{ fontSize: '12px', color: '#856404', marginTop: '12px', textAlign: 'center' }}>
        ‚ö†Ô∏è Hints add time penalties but don't affect your final ranking position
      </div>
    </div>
  );
};

export default HintSystem;
