import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Teams = () => {
  const [teams, setTeams] = useState([]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newTeam, setNewTeam] = useState({ name: '', description: '' });
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [teamMembers, setTeamMembers] = useState([]);
  const [newMemberEmail, setNewMemberEmail] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTeams();
  }, []);

  const fetchTeams = async () => {
    try {
      const response = await api.get('/teams');
      setTeams(response.data);
    } catch (error) {
      console.error('Error fetching teams:', error);
    } finally {
      setLoading(false);
    }
  };

  const createTeam = async (e) => {
    e.preventDefault();
    try {
      await api.post('/teams', newTeam);
      setNewTeam({ name: '', description: '' });
      setShowCreateForm(false);
      fetchTeams();
    } catch (error) {
      console.error('Error creating team:', error);
    }
  };

  const fetchTeamMembers = async (teamId) => {
    try {
      const response = await api.get(`/teams/${teamId}/members`);
      setTeamMembers(response.data);
      setSelectedTeam(teamId);
    } catch (error) {
      console.error('Error fetching team members:', error);
    }
  };

  const addMember = async (e) => {
    e.preventDefault();
    try {
      await api.post(`/teams/${selectedTeam}/members`, { email: newMemberEmail });
      setNewMemberEmail('');
      fetchTeamMembers(selectedTeam);
    } catch (error) {
      console.error('Error adding member:', error);
      alert(error.response?.data?.error || 'Error adding member');
    }
  };

  if (loading) {
    return <div className="container">Loading...</div>;
  }

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h1>Teams</h1>
        <button 
          onClick={() => setShowCreateForm(!showCreateForm)} 
          className="btn btn-primary"
        >
          Create Team
        </button>
      </div>

      {showCreateForm && (
        <div className="card">
          <h2>Create New Team</h2>
          <form onSubmit={createTeam}>
            <div className="form-group">
              <label>Team Name</label>
              <input
                type="text"
                value={newTeam.name}
                onChange={(e) => setNewTeam({ ...newTeam, name: e.target.value })}
                required
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <input
                type="text"
                value={newTeam.description}
                onChange={(e) => setNewTeam({ ...newTeam, description: e.target.value })}
              />
            </div>
            <button type="submit" className="btn btn-primary">Create</button>
            <button 
              type="button" 
              onClick={() => setShowCreateForm(false)}
              style={{ marginLeft: '10px' }}
            >
              Cancel
            </button>
          </form>
        </div>
      )}

      <div className="team-list">
        {teams.map(team => (
          <div key={team.id} className="card">
            <h3>{team.name}</h3>
            <p>{team.description}</p>
            <p><strong>Role:</strong> {team.role}</p>
            <button 
              onClick={() => fetchTeamMembers(team.id)}
              className="btn btn-primary"
            >
              View Members
            </button>
          </div>
        ))}
      </div>

      {selectedTeam && (
        <div className="card">
          <h2>Team Members</h2>
          <div style={{ marginBottom: '20px' }}>
            {teamMembers.map(member => (
              <div key={member.id} style={{ padding: '10px', borderBottom: '1px solid #eee' }}>
                <strong>{member.name}</strong> ({member.email}) - {member.role}
              </div>
            ))}
          </div>

          <form onSubmit={addMember}>
            <div className="form-group">
              <label>Add Member by Email</label>
              <input
                type="email"
                value={newMemberEmail}
                onChange={(e) => setNewMemberEmail(e.target.value)}
                placeholder="Enter email address"
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">Add Member</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Teams;
