import React, { useState, useEffect } from 'react';
import api from '../services/api';

const LiveLeaderboardPage = () => {
  const [leaderboard, setLeaderboard] = useState([]);

  useEffect(() => {
    fetchLeaderboard();
  }, []);

  const fetchLeaderboard = async () => {
    try {
      const response = await api.get('/leaderboard');
      setLeaderboard(response.data);
    } catch (error) {
      console.error('Error fetching leaderboard:', error);
    }
  };

  return (
    <div className="container">
      <h1>Live Leaderboard</h1>

      <div className="card">
        <h2>Top Teams</h2>
        {leaderboard.length === 0 ? (
          <p>No quiz submissions yet</p>
        ) : (
          <div>
            {leaderboard.map((team, index) => (
              <div key={index} className="card" style={{ 
                marginBottom: '16px',
                backgroundColor: index === 0 ? '#fff3cd' : index === 1 ? '#e2e3e5' : index === 2 ? '#f8d7da' : 'white'
              }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div>
                    <h3>#{index + 1} {team.team_name}</h3>
                    <p><strong>Quiz:</strong> {team.quiz_title}</p>
                  </div>
                  <div style={{ textAlign: 'right' }}>
                    <h4>{team.score} / {team.total_questions}</h4>
                    <p>{team.percentage}%</p>
                    <small>{new Date(team.submitted_at).toLocaleString()}</small>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default LiveLeaderboardPage;
