import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api';

const QuizTaker = () => {
  const { link } = useParams();
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [teamName, setTeamName] = useState('');
  const [result, setResult] = useState(null);

  useEffect(() => {
    fetchQuiz();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [link]);

  const fetchQuiz = async () => {
    try {
      const response = await api.get(`/quiz/${link}`);
      setQuiz(response.data);
      setAnswers(new Array(response.data.questions.length).fill(''));
    } catch (error) {
      alert('Quiz not found');
    }
  };

  const submitQuiz = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post(`/quiz/${link}/submit`, {
        teamName,
        answers
      });
      setResult(response.data);
    } catch (error) {
      alert('Failed to submit quiz');
    }
  };

  if (result) {
    return (
      <div className="container">
        <h2>Quiz Completed!</h2>
        <p>Your Score: {result.score} / {result.total}</p>
        <p>Percentage: {Math.round((result.score / result.total) * 100)}%</p>
      </div>
    );
  }

  if (!quiz) {
    return <div className="container">Loading...</div>;
  }

  return (
    <div className="container">
      <h2>{quiz.quiz.title}</h2>
      <p>{quiz.quiz.description}</p>

      <form onSubmit={submitQuiz}>
        <div className="form-group">
          <label>Team Name</label>
          <input
            type="text"
            value={teamName}
            onChange={(e) => setTeamName(e.target.value)}
            placeholder="Enter your team name"
            required
          />
        </div>

        {quiz.questions.map((question, index) => (
          <div key={index} className="card" style={{ marginBottom: '16px' }}>
            <h4>Question {index + 1}</h4>
            <p>{question.question_text}</p>
            <input
              type="text"
              value={answers[index]}
              onChange={(e) => {
                const newAnswers = [...answers];
                newAnswers[index] = e.target.value;
                setAnswers(newAnswers);
              }}
              required
            />
          </div>
        ))}

        <button type="submit" className="btn btn-primary">
          Submit Quiz
        </button>
      </form>
    </div>
  );
};

export default QuizTaker;
