import React, { useState, useEffect } from 'react';
import api from '../services/api';
import LoadingSpinner from '../components/LoadingSpinner';

const Teams = () => {
  const [teams, setTeams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingTeam, setEditingTeam] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [formData, setFormData] = useState({ name: '', description: '' });

  useEffect(() => {
    fetchTeams();
  }, []);

  const fetchTeams = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await api.get('/api/teams');
      console.log('Teams response:', response.data);
      setTeams(response.data || []);
      setError(''); // Clear any previous errors
    } catch (error) {
      console.error('Error fetching teams:', error);
      setError('Failed to load teams. Please try again.');
      setTeams([]); // Clear teams on error
    } finally {
      setLoading(false);
    }
  };

  const handleCreateTeam = async (e) => {
    e.preventDefault();
    try {
      await api.post('/api/teams', formData);
      setFormData({ name: '', description: '' });
      setShowCreateForm(false);
      fetchTeams();
    } catch (error) {
      setError('Failed to create team');
    }
  };

  const handleUpdateTeam = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/api/teams/${editingTeam.id}`, formData);
      setEditingTeam(null);
      setFormData({ name: '', description: '' });
      fetchTeams();
    } catch (error) {
      setError('Failed to update team');
    }
  };

  const handleDeleteTeam = async (teamId) => {
    if (window.confirm('Are you sure you want to delete this team?')) {
      try {
        await api.delete(`/api/teams/${teamId}`);
        fetchTeams();
      } catch (error) {
        setError('Failed to delete team');
      }
    }
  };

  const startEdit = (team) => {
    setEditingTeam(team);
    setFormData({ name: team.name, description: team.description || '' });
  };

  if (loading) {
    return (
      <div className="container">
        <LoadingSpinner size="large" text="Loading teams..." />
      </div>
    );
  }

  return (
    <div className="container">
      <h1 className="welcome-title" style={{ fontSize: '2.5rem', marginBottom: '40px' }}>
        üë• Teams Dashboard
      </h1>

      {error && (
        <div className="error-message">
          ‚ö†Ô∏è {error}
        </div>
      )}

      <div className="card fade-in">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
          <h2 style={{ 
            fontSize: '1.8rem',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            üèÜ All Teams ({teams.length})
          </h2>
          <div style={{ display: 'flex', gap: '12px' }}>
            <button 
              onClick={() => setShowCreateForm(true)}
              className="btn btn-primary"
              style={{ padding: '8px 16px', fontSize: '14px' }}
            >
              ‚ûï Add Team
            </button>
            <button 
              onClick={fetchTeams}
              className="btn btn-primary"
              style={{ padding: '8px 16px', fontSize: '14px' }}
            >
              üîÑ Refresh
            </button>
          </div>
        </div>

        {/* Create/Edit Form */}
        {(showCreateForm || editingTeam) && (
          <div className="card" style={{ marginBottom: '30px', background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.05) 0%, rgba(118, 75, 162, 0.05) 100%)' }}>
            <h3>{editingTeam ? '‚úèÔ∏è Edit Team' : '‚ûï Create New Team'}</h3>
            <form onSubmit={editingTeam ? handleUpdateTeam : handleCreateTeam}>
              <div className="form-group">
                <label>Team Name</label>
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  required
                  placeholder="Enter team name"
                />
              </div>
              <div className="form-group">
                <label>Description</label>
                <input
                  type="text"
                  value={formData.description}
                  onChange={(e) => setFormData({...formData, description: e.target.value})}
                  placeholder="Enter team description"
                />
              </div>
              <div style={{ display: 'flex', gap: '12px' }}>
                <button type="submit" className="btn btn-primary">
                  {editingTeam ? 'üíæ Update' : '‚úÖ Create'}
                </button>
                <button 
                  type="button" 
                  onClick={() => {
                    setShowCreateForm(false);
                    setEditingTeam(null);
                    setFormData({ name: '', description: '' });
                  }}
                  className="btn"
                  style={{ background: '#e2e8f0', color: '#4a5568' }}
                >
                  ‚ùå Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        {teams.length === 0 ? (
          <div style={{ 
            textAlign: 'center', 
            padding: '60px 20px',
            background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.05) 0%, rgba(118, 75, 162, 0.05) 100%)',
            borderRadius: '12px',
            border: '2px dashed rgba(102, 126, 234, 0.2)'
          }}>
            <div style={{ fontSize: '4rem', marginBottom: '20px' }}>üë•</div>
            <h3 style={{ color: '#4a5568', marginBottom: '12px' }}>No Teams Found</h3>
            <p style={{ color: '#718096' }}>Teams will appear here once they are created.</p>
          </div>
        ) : (
          <div className="team-list">
            {teams.map((team, index) => (
              <div 
                key={team.id} 
                className="card hover-lift fade-in" 
                style={{ 
