import React, { useEffect } from 'react';

const SecurityWrapper = ({ children, disableCopy = false, disableRightClick = false }) => {
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (disableCopy) {
        // Disable Ctrl+C, Ctrl+A, Ctrl+V, F12, Ctrl+Shift+I
        if (
          (e.ctrlKey && (e.key === 'c' || e.key === 'a' || e.key === 'v')) ||
          e.key === 'F12' ||
          (e.ctrlKey && e.shiftKey && e.key === 'I') ||
          (e.ctrlKey && e.shiftKey && e.key === 'J') ||
          (e.ctrlKey && e.key === 'u')
        ) {
          e.preventDefault();
          return false;
        }
      }
    };

    const handleContextMenu = (e) => {
      if (disableRightClick) {
        e.preventDefault();
        return false;
      }
    };

    const handleSelectStart = (e) => {
      if (disableCopy) {
        e.preventDefault();
        return false;
      }
    };

    const handleDragStart = (e) => {
      if (disableCopy) {
        e.preventDefault();
        return false;
      }
    };

    if (disableCopy || disableRightClick) {
      document.addEventListener('keydown', handleKeyDown);
      document.addEventListener('contextmenu', handleContextMenu);
      document.addEventListener('selectstart', handleSelectStart);
      document.addEventListener('dragstart', handleDragStart);

      // Disable text selection via CSS
      document.body.style.userSelect = disableCopy ? 'none' : 'auto';
      document.body.style.webkitUserSelect = disableCopy ? 'none' : 'auto';
      document.body.style.mozUserSelect = disableCopy ? 'none' : 'auto';
      document.body.style.msUserSelect = disableCopy ? 'none' : 'auto';
    }

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('contextmenu', handleContextMenu);
      document.removeEventListener('selectstart', handleSelectStart);
      document.removeEventListener('dragstart', handleDragStart);

      // Reset styles
      document.body.style.userSelect = 'auto';
      document.body.style.webkitUserSelect = 'auto';
      document.body.style.mozUserSelect = 'auto';
      document.body.style.msUserSelect = 'auto';
    };
  }, [disableCopy, disableRightClick]);

  return (
    <div style={{ 
      userSelect: disableCopy ? 'none' : 'auto',
      WebkitUserSelect: disableCopy ? 'none' : 'auto',
      MozUserSelect: disableCopy ? 'none' : 'auto',
      msUserSelect: disableCopy ? 'none' : 'auto'
    }}>
      {children}
    </div>
  );
};

export default SecurityWrapper;
