import React, { useState } from 'react';
import api from '../services/api';

const QuizCreator = () => {
  const [quiz, setQuiz] = useState({
    title: '',
    description: '',
    totalTimeMinutes: 30,
    questions: [{ question: '', answer: '', timeLimit: 120 }]
  });
  const [createdQuiz, setCreatedQuiz] = useState(null);

  const addQuestion = () => {
    setQuiz({
      ...quiz,
      questions: [...quiz.questions, { question: '', answer: '', timeLimit: 120 }]
    });
  };

  const updateQuestion = (index, field, value) => {
    const newQuestions = [...quiz.questions];
    newQuestions[index][field] = value;
    setQuiz({ ...quiz, questions: newQuestions });
  };

  const createQuiz = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/quiz/create', quiz);
      setCreatedQuiz(response.data);
    } catch (error) {
      alert('Failed to create quiz');
    }
  };

  const publishQuiz = async () => {
    try {
      await api.post(`/quiz/${createdQuiz.quiz.id}/publish`);
      alert('Quiz published!');
    } catch (error) {
      alert('Failed to publish quiz');
    }
  };

  if (createdQuiz) {
    return (
      <div className="container">
        <h2>Quiz Created!</h2>
        <p><strong>Title:</strong> {createdQuiz.quiz.title}</p>
        <p><strong>Link:</strong> {window.location.origin}{createdQuiz.link}</p>
        <button onClick={publishQuiz} className="btn btn-primary">
          Publish Quiz
        </button>
        <button onClick={() => setCreatedQuiz(null)} className="btn">
          Create Another
        </button>
      </div>
    );
  }

  return (
    <div className="container">
      <h2>Create Quiz</h2>
      <form onSubmit={createQuiz}>
        <div className="form-group">
          <label>Quiz Title</label>
          <input
            type="text"
            value={quiz.title}
            onChange={(e) => setQuiz({ ...quiz, title: e.target.value })}
            required
          />
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea
            value={quiz.description}
            onChange={(e) => setQuiz({ ...quiz, description: e.target.value })}
          />
        </div>

        <div className="form-group">
          <label>Total Quiz Time (minutes)</label>
          <input
            type="number"
            value={quiz.totalTimeMinutes}
            onChange={(e) => setQuiz({ ...quiz, totalTimeMinutes: parseInt(e.target.value) })}
            min="1"
            required
          />
        </div>

        <h3>Questions</h3>
        {quiz.questions.map((q, index) => (
          <div key={index} className="card" style={{ marginBottom: '16px' }}>
            <div className="form-group">
              <label>Question {index + 1}</label>
              <input
                type="text"
                value={q.question}
                onChange={(e) => updateQuestion(index, 'question', e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Answer</label>
              <input
                type="text"
                value={q.answer}
                onChange={(e) => updateQuestion(index, 'answer', e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Time Limit (seconds)</label>
              <input
                type="number"
                value={q.timeLimit}
                onChange={(e) => updateQuestion(index, 'timeLimit', parseInt(e.target.value))}
                min="10"
                required
              />
            </div>
          </div>
        ))}

        <button type="button" onClick={addQuestion} className="btn">
          Add Question
        </button>

        <button type="submit" className="btn btn-primary" style={{ marginLeft: '16px' }}>
          Create Quiz
        </button>
      </form>
    </div>
  );
};

export default QuizCreator;
